SELECT DISTINCT(people.name) FROM movies, people, stars WHERE stars.person_id = people.id AND movies.id = stars.movie_id AND movies.id IN (SELECT movies.id FROM movies, stars, people WHERE people.id = stars.person_id AND movies.id = stars.movie_id AND people.name = 'Kevin Bacon' AND people.birth = 1958) AND people.name != 'Kevin Bacon';